// src/components/TopHeader.js
import React from 'react';
import '../style/style.css';  // Import your custom CSS

// Import Swiper core styles
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

// Import necessary Swiper components
import { Swiper, SwiperSlide } from 'swiper/react';  // Import Swiper and SwiperSlide components

const TopHeader = () => {
  return (
    <div className="top_header">
      <Swiper
        className="mySwiper top_balck_header"
        spaceBetween={50}   // Space between slides
        slidesPerView={1}   // One slide at a time
        loop={true}         // Loop the slides
        autoplay={{
          delay: 10,            // Set the delay to 1 second (1000ms)
          disableOnInteraction: false,  // Allow autoplay to continue even when the user interacts with the slider
        }}
        pagination={{ clickable: true }}  // Enable pagination dots
        navigation={false}     // Disable navigation buttons
        grabCursor={true}      // Enable drag/swipe functionality
      >
        <SwiperSlide>
          <span>Shop for Rs 2499/- & get 10% OFF</span>
          <span className="sec_span">Use Code: SHOP10</span>
        </SwiperSlide>
        <SwiperSlide>
          <span>Shop for Rs 3999/- & Get FLAT 15% OFF</span>
          <span className="sec_span">Use Code: SHOP15</span>
        </SwiperSlide>
        <SwiperSlide>
          <span>Flat 25% off | USE CODE: FINALCALL</span>
          <span className="sec_span"><a href="#">CLICK HERE</a></span>
        </SwiperSlide>
      </Swiper>
    </div>
  );
}

export default TopHeader;
