import React from 'react';
import 'src/style/style.css';  // Import the corresponding CSS for this section
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';


// Import Swiper and SwiperSlide components
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Navigation, Pagination } from 'swiper';

// Install necessary Swiper modules
SwiperCore.use([Navigation, Pagination]);

function TopHeader() {
  return (
    <div className="top_header">
      <Swiper
        className="mySwiper top_balck_header"
        spaceBetween={50}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
      >
        <SwiperSlide>
          <span>Shop for Rs 2499/- & get 10% OFF</span>
          <span className="sec_span">Use Code: SHOP10</span>
        </SwiperSlide>
        <SwiperSlide>
          <span>Shop for Rs 3999/- & Get FLAT 15% OFF</span>
          <span className="sec_span">Use Code: SHOP15</span>
        </SwiperSlide>
        <SwiperSlide>
          <span>flat 25% off | USE CODE: FINALCALL</span>
          <span className="sec_span"><a href="#">CLICK HERE</a></span>
        </SwiperSlide>
      </Swiper>
    </div>
  );
}

export default TopHeader;
